let ma_valeur = global.get('button2');
let msg1 = null;
let msg2 = null;
// --- Logique principale : Si la condition globale est remplie ---
if(ma_valeur === "up")
{
    let ma_valeur2 = global.get('boucle');
    // On vérifie le message entrant (l'émotion)
    if(msg.payload === "fear")
    {
        msg.payload = "on";
        msg1=msg;
        if(ma_valeur2==="off")
        {
            msg.payload="on"
            msg2=msg;

        }
    }
    else // Toute autre émotion
    {
        msg.payload = "off";
        msg1 = msg;
    }



    // Le message est modifié et renvoyé à la sortie 1
    return [msg1, msg2];
}

// --- Logique secondaire : Si la condition globale n'est pas remplie ---
else
{
    // Si la valeur n'est PAS "up", on ne fait rien ou on envoie "off".
    // Si vous voulez envoyer "off" :
    msg.payload = "off";

    return [msg, msg];

